function injectChatIcon() {
    try {

		//is Next Experience UI loaded?
        if (!(top.NOW.isPolarisWrapper === "true")) {
            return;
        }
        //is the icon already injected?
        // if (typeof window.top.chat_icon_injected !== 'undefined') {
        //     return;
        // }
        // à¹„à¸¡à¹ˆà¸ªà¸£à¹‰à¸²à¸‡à¸‹à¹‰à¸³
        if (document.getElementById("sn-chat-icon")) return;

        // --- à¸ªà¸£à¹‰à¸²à¸‡ CSS
        var style = document.createElement("style");
        style.textContent = `
    #sn-chat-icon {
      position: fixed;
      bottom: 20px;
      right: 20px;
      width: 60px;
      height: 60px;
      background-color: #0056b3;
      border-radius: 50%;
      color: white;
      font-size: 28px;
      text-align: center;
      line-height: 60px;
      cursor: pointer;
      z-index: 9999;
      box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
      transition: background-color 0.3s, transform 0.2s;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    #sn-chat-icon:hover {
      background-color: #004494;
      transform: scale(1.05);
    }
    #sn-chat-modal {
      display: none;
      position: fixed;
      bottom: 90px;
      right: 20px;
      width: 420px;
      height: 630px;
      background-color: #ffffff;
      border-radius: 16px;
      box-shadow: 0 12px 32px rgba(0, 0, 0, 0.3);
      z-index: 9998;
      overflow: hidden;
      animation: fadeInUp 0.3s ease-out;
      border: 1px solid #cdd6e0;
      transition: display 0.2s;
    }
    .sn-close-btn {
      position: absolute;
      top: 11px;
      right: 14px;
      font-size: 15px;
      color: #888;
      cursor: pointer;
    }
    .sn-close-btn:hover {
      color: #333;
    }
    #sn-chat-modal iframe {
      width: 100%;
      height: 100%;
      border: none;
      background: transparent;
    }
    @keyframes fadeInUp {
      from {
        opacity: 0;
        transform: translateY(20px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }
    @media (max-width: 500px) {
      #sn-chat-modal {
        width: 90vw;
        height: 80vh;
        bottom: 80px;
        right: 5vw;
      }
    }
  `;
        document.head.appendChild(style);

        // --- HTML: icon + modal
        var iconDiv = document.createElement("div");
        iconDiv.id = "sn-chat-icon";
        iconDiv.title = "Open Chat";
        iconDiv.innerHTML = "ðŸ’¬";
        document.body.appendChild(iconDiv);

        var modalDiv = document.createElement("div");
        modalDiv.id = "sn-chat-modal";
        modalDiv.innerHTML = `
    <button class="sn-close-btn" title="Close">&times;</button>
    <iframe src="/chat_ui.do"></iframe>
  `;
        document.body.appendChild(modalDiv);

        // --- event & function
        var chatIcon = iconDiv;
        var chatModal = modalDiv;
        var closeBtn = modalDiv.querySelector(".sn-close-btn");
        var chatIframe = modalDiv.querySelector("iframe");

        // à¸Ÿà¸±à¸‡à¸à¹Œà¸Šà¸±à¸™à¹€à¸£à¸µà¸¢à¸à¸à¸±à¹ˆà¸‡ iframe à¹ƒà¸«à¹‰ scroll à¹„à¸›à¸¥à¹ˆà¸²à¸‡à¸ªà¸¸à¸”
        function scrollIframeChatToBottom() {
            if (chatIframe) {
                try {
                    chatIframe.contentWindow.scrollChatboxToBottom(); // function à¸™à¸µà¹‰à¸„à¸§à¸£à¸¡à¸µà¹ƒà¸™ chat_ui.do
                } catch (e) {
                    // à¸–à¹‰à¸² cross domain à¸ˆà¸° error
                }
            }
        }

        // toggle à¹€à¸›à¸´à¸”/à¸›à¸´à¸” modal à¸–à¹‰à¸²à¸à¸” icon
        function toggleChatModal() {
            if (chatModal.style.display === "block") {
                chatModal.style.display = "none";
            } else {
                chatModal.style.display = "block";
                setTimeout(scrollIframeChatToBottom, 350); // à¸£à¸­ modal & iframe ready
            }
        }

        chatIcon.addEventListener("click", toggleChatModal);
        closeBtn.addEventListener("click", () => {
            chatModal.style.display = "none";
        });
        // à¸›à¸´à¸”à¹€à¸¡à¸·à¹ˆà¸­à¸à¸” ESC
        document.addEventListener("keydown", function(event) {
            if (event.key === "Escape") {
                chatModal.style.display = "none";
            }
        });

	// top.chat_icon_injected = true;

    } catch (e) {
        console.error(e);
    }
}

//after finished loading the page inject custom icon



      if (typeof jQuery === 'function') {
		jQuery(document).ready(injectChatIcon);
	}
